import React, { useState } from 'react';

import "./ImageSlider.scss";

const ImageSlider = ({ children, onclickEvent }) => {

    const [activeId, setActiveId] = useState(0);

    const handleClick = (e) => {
        if (activeId === e.target.id) {
            setActiveId(activeId+1);
        }
    }

    return (
        <div className="ImageSlider mini-container" onClick={handleClick}>
            {children.map((child, id) => (
                <div className={`ImageSlider-slide ${id === activeId ? "active" : ""}`} key={id}>{child}</div>
            ))}

        </div>
    );
}

export default ImageSlider;
