import React, { useState } from 'react';

import "./ImageSlider.scss";

const ImageSlider = ({ children }) => {

    const [activeId, setActiveId] = useState(true);

    const handleClick = (e) => {
        if (activeId === e.target.id) {
            setActiveId(true);
        } else {
            setActiveId(false);
        }
    }

    return (
        <div className="ImageSlider mini-container" >
            {children.map((child, id) => {
                <div className={`ImageSlider-slide ${id === activeId ? "active" : ""}`}>{child}</div>
})}

        </div>
    );
}

export default ImageSlider;
