import React, { useState } from 'react';
import Slider from 'react-slick';
import Modal from 'react-modal';

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

Modal.setAppElement('#root');

const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 3,
  slidesToScroll: 3,
  autoplay: true,
  autoplaySpeed: 3000,
};

const cities = [
  { img: "", name: "Dubai", population: "Population: 2M" },
  // Qo'shimcha shaharlar bu yerda
];

const ImageSlider = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedImg, setSelectedImg] = useState('');

  const openModal = (img) => {
    setSelectedImg(img);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div style={{ width: '100%', padding: '20px' }}>
      <Slider {...settings}>
        {cities.map(city => (
          <div key={city.name} onClick={() => openModal(city.img)}>
            <img src={city.img} alt={city.name} style={{ width: '100%', height: 'auto' }} />
            <p>{`${city.name} - ${city.population}`}</p>
          </div>
        ))}
      </Slider>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Image Modal"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            width: '80%',
            height: 'auto',
          }
        }}
      >
        <img src={selectedImg} alt="Enlarged view" style={{ width: '100%' }} />
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
}

export default ImageSlider;
