import React from 'react'
import MainTitle from "../../components/MainTitle/MainTitle";
import Card from "../../components/Card/Card";
import { CardIcon2, CardIcon3, CardIcon4 } from "../../assets/icons";

import "./Prices.scss";

const data = [
    {
        id: 0,
        cityName: "Istanbul",
        users: "234",
        price: "$420",
        items: [
            {
                icon: <CardIcon2 />,
                text: "5 Days Trip > Hotel Included"
            },

            {
                icon: <CardIcon3 />,
                text: "Airplane Bill Included"
            },

            {
                icon: <CardIcon4 />,
                text: "Daily Places Visit"
            }
        ]
    },

    {
        id: 1,
        cityName: "Dubai",
        users: "300",
        price: "$700",
        items: [
            {
                icon: <CardIcon2 />,
                text: "5 Days Trip > Hotel Included"
            },

            {
                icon: <CardIcon3 />,
                text: "Airplane Bill Included"
            },

            {
                icon: <CardIcon4 />,
                text: "Daily Places Visit"
            }
        ]
    },

    {
        id: 2,
        cityName: "Antalya",
        users: "500",
        price: "$999",
        items: [
            {
                icon: <CardIcon2 />,
                text: "5 Days Trip > Hotel Included"
            },

            {
                icon: <CardIcon3 />,
                text: "Airplane Bill Included"
            },

            {
                icon: <CardIcon4 />,
                text: "Daily Places Visit"
            }
        ]
    },
]

const Prices = () => {
    return (
        <div className="Prices container">
            <MainTitle
                title="Best Weekly Offers In Each City"
                text="Discover the best weekly offers in each city, curated just for you.
                Immerse yourself in a world of savings and indulge in unparalleled experiences."
            />

            <div className="Prices-cards">
                {
                    data.map(el => (
                        <Card
                            key={el.id}
                            icon={el.icon}
                            price={el.price}
                            cityName={el.cityName}
                            users={el.users}
                            items={[...el.items]}
                        />
                    ))
                }
            </div>

            <Swiper
                slidesPerView={1}
                spaceBetween={10}
                pagination={{
                    clickable: true,
                }}
                breakpoints={{
                    640: {
                        slidesPerView: 2,
                        spaceBetween: 20,
                    },
                    768: {
                        slidesPerView: 4,
                        spaceBetween: 40,
                    },
                    1024: {
                        slidesPerView: 5,
                        spaceBetween: 50,
                    },
                }}
                modules={[Pagination]}
                className="mySwiper"
            >
                <SwiperSlide>Slide 1</SwiperSlide>
                <SwiperSlide>Slide 2</SwiperSlide>
                <SwiperSlide>Slide 3</SwiperSlide>
                <SwiperSlide>Slide 4</SwiperSlide>
                <SwiperSlide>Slide 5</SwiperSlide>
                <SwiperSlide>Slide 6</SwiperSlide>
                <SwiperSlide>Slide 7</SwiperSlide>
                <SwiperSlide>Slide 8</SwiperSlide>
                <SwiperSlide>Slide 9</SwiperSlide>
            </Swiper>

        </div>
    )
}

export default Prices;
