import React, { useState } from 'react';

import "./ImageSlider.scss";

const ImageSlider = ({ children, onclickEvent }) => {

    const [activeId, setActiveId] = useState(true);
    const [ev, setEv] = useState("");

    const handleClick = (e) => {

        if (activeId === e.target.id) {
            setActiveId(true);
        } else {
            setActiveId(false);
        }
    }

    return (
        <div className="ImageSlider mini-container">
            {children.map((child, id) => {
                <div className={`ImageSlider-slide ${id === activeId ? "active" : ""}`} key={id}>{child}</div>
            })}

        </div>
    );
}

export default ImageSlider;
